import React, { useState, useEffect } from 'react'; 
import axios from 'axios'; 
import 'bootstrap/dist/css/bootstrap.min.css'; 
import {  useNavigate } from 'react-router-dom'; 
import {Bar} from 'react-chartjs-2';
import {Chart as ChartJS, CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend, plugins} from 'chart.js';

ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);

const Logs = () => {   
    const navigate = useNavigate();
    const handleClick = () => {
        navigate('/home'); // Redirige a la página de logs
      };
      const [logs, setLogs] = useState([]);
      const [chartData, setChartData] = useState(null);

    useEffect(() =>  {
      axios.get("http://localhost:5001/getServer")
          .then(response => {
            setLogs(response.data.logs);
            procesarDatos(response.data.logs);
          })
          .catch(error => console.error("Error obteniendo logs: ", error));
    },[]);

    const procesarDatos = (logs) => {
      const cantidadServidor1 = logs.filter(log => log.server === "1").length;
      const cantidadServidor2 = logs.filter(log => log.server === "2").length;

      console.log(cantidadServidor1);
      console.log(cantidadServidor2);
      const labels = ["Servidor 1", "Servidor 2"];

      const valores = [cantidadServidor1, cantidadServidor2]; 

      setChartData({
        labels,
        datasets: [
          {
            label: "Cantidad de Logs por Servidor",
            data: valores,
            backgroundColor: ["rgba(54, 162, 235, 0.5)", "rgba(255, 99, 132, 0.5)"],
            borderColor: ["rgba(54, 162, 235, 1)", "rgba(255, 99, 132, 1)"],
            borderWidth: 1,
          } 
        ]
      });
    };

    return (
        <div className='container mt-4'>
            <h3 className='text-center'>Logs</h3> 
            <div className="card p-3">
              {chartData ? (
                <Bar data={chartData} options={{responsive: true, plugins: {legend: { position: 'top' }}}} />
              ): (
                <p>Cargando datos...</p>
              )}
            </div>

            <button className="btn btn-warning" style = {{ marginLeft: '10%'}}  type="button" onClick={handleClick}>Home</button>
        </div>
        
      );
};

export default Logs;
