import React, { useState, useEffect } from 'react'; 
import axios from 'axios'; 
import 'bootstrap/dist/css/bootstrap.min.css'; 
import { data, useNavigate } from 'react-router-dom'; 
import {Bar} from 'react-chartjs-2';
import {Chart as ChartJS, CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend, plugins} from 'chart.js';

ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);

const Logs = () => {   
    const navigate = useNavigate();
    const handleClick = () => {
        navigate('/home'); // Redirige a la página de logs
      };
      const [logs, setLogs] = useState([]);
      const [chartData, setChartData] = useState(null);

    useEffect(() =>  {
      axios.get("http://localhost:5001/getServer")
          .then(response => {
            setLogs(response.data.logs);
            procesarDatos(response.data.logs);
          })
          .catch(error => console.error("Error obteniendo logs: ", error));
    },[]);

    const procesarDatos = (logs) => {
      const logsServidor1 = logs.filter(log => log.servidor === "1");
      const logsServidor2 = logs.filter(log => log.servidor === "2");

      const labels = logs.map((log, index) =>`Log ${index + 1}`);

      const valoresServidor1 = logsServidor1.map(log => log.valor || 0);
      const valoresServidor2 = logsServidor2.map(log => log.valor || 0);

      setChartData({
        labels,
        datasets: [
          {
            label: 'Servidor 1',
            data: valoresServidor1,
            backgroundColor: 'rgba(255, 99, 132, 0.5)',
            borderColor: 'rgba(255, 99, 132, 1)',
            borderWith: 1,
          }
        ]
      });
    };

    return (
        <div className='container mt-4'>
            <h3 className='text-center'>Logs</h3> 
            <div className="card p-3">
              {chartData ? (
                <Bar data={chartData} options={{responsive: true, plugins: {legnd: {position: 'top'}}}} />
              ): (
                <p>Cargando datos...</p>
              )}
            </div>

            <button className="btn btn-warning" style = {{ marginLeft: '10%'}}  type="button" onClick={handleClick}>Home</button>
        </div>
        
      );
};

export default Logs;
