import React, { useState, useEffect } from 'react'; 
import axios from 'axios'; 
import 'bootstrap/dist/css/bootstrap.min.css';
import { useLocation } from 'react-router-dom'; 

const Home = () => {  
    const [ userData, setUserData]  = useState();
    const location = useLocation();
    const params = new URLSearchParams(location.search);
    const idUs = params.get("idUs");
    
    useEffect(() => {
        if(idUs){
            handleGetUser();
        }
    }, [idUs]);
    
    const handleGetUser = async () => { 
        try{
            const res = await axios.get(`http://localhost:5001/getInfo?idUs=${idUs}`); 
            setUserData(res.data.user); 
            }catch(error){
            alert("Error al obtener la información del usuario");
            }

    }; 
    
    return (
        <div className ="card" style = {{ width: '450px', marginLeft: '35%', marginTop: '6%' , marginBottom: '10%'}}>
            <div className ="card-body">    
                        {userData && (
                            <div>
                                <h3>Información del Usuario:</h3>
                                <p>Nombre completo: {userData.nombre}</p>
                                <p>email: {userData.email}</p> 
                            </div>
                        )} 
            </div> 
        </div>
      );
};

export default Home;
