import React, { useState, useEffect } from 'react'; 
import axios from 'axios'; 
import 'bootstrap/dist/css/bootstrap.min.css';
import { useLocation, useNavigate } from 'react-router-dom'; 

const Home = () => {  
    const navigate = useNavigate();
    const [ userData, setUserData]  = useState();
    const location = useLocation();
    const params = new URLSearchParams(location.search);
    const idUs = params.get("idUs");

    const handleClick = () => {
        navigate('/logs'); // Redirige a la página de logs
      };
    
    useEffect(() => {
        if(idUs){
            handleGetUser();
        }
    }, [idUs]);
    
    const handleGetUser = async () => { 
        try{
            const res = await axios.get(`http://localhost:5001/getInfo?idUs=${idUs}`); 
            setUserData(res.data.user); 
            }catch(error){
            alert("Error al obtener la información del usuario");
            }

    }; 
    
    return (
        <div className ="card" style = {{ width: '450px', marginLeft: '35%', marginTop: '6%' , marginBottom: '10%'}}>
            <div className ="card-body">    
                        {userData && (
                            <div>
                                <h3>Información del Usuario:</h3>
                                <p>Nombre completo: {userData.nombre} {userData.app} {userData.apm}</p>
                                <p>Grupo: {userData.grupo}</p> 
                            </div>
                        )} 
            </div> 
            
            <button className="btn btn-warning" style = {{ marginLeft: '110px'}}  type="button" onClick={handleClick}>Logs</button>
        </div>
      );
};

export default Home;
