import React, { useState, useEffect } from 'react'; 
import axios from 'axios'; 
import 'bootstrap/dist/css/bootstrap.min.css'; 
import { useNavigate } from 'react-router-dom'; 
import { Bar } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend } from 'chart.js';

ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);

const Logs = () => {   
    const navigate = useNavigate();
    const [logs, setLogs] = useState([]);
    const [chartData, setChartData] = useState(null);

    useEffect(() => {
        axios.get("http://localhost:5001/getServer")
            .then(response => {
                if (response.data.logs && response.data.logs.length > 0) {
                    setLogs(response.data.logs);
                    procesarDatos(response.data.logs);
                }
            })
            .catch(error => console.error("Error obteniendo logs: ", error));
    }, []);

    console.log(logs);

    const procesarDatos = (logs) => {
      let cantidadServidor1_info = 0;
      let cantidadServidor1_warn = 0;
      let cantidadServidor1_error = 0;
  
      let cantidadServidor2_info = 0;
      let cantidadServidor2_warn = 0;
      let cantidadServidor2_error = 0;

      logs.forEach(log => {
        if(log.server === "1" || log.server === 1 ){
          if([200,201,204].includes(log.status)) cantidadServidor1_info ++;
          else if([300,301,302].includes(log.status)) cantidadServidor1_warn ++;
          else if([log.status >= 400]) cantidadServidor1_error ++;
        }
        if(log.server === "2" || log.server === 2 ){
          if([200,201,204].includes(log.status)) cantidadServidor1_info ++;
          else if([300,301,302].includes(log.status)) cantidadServidor1_warn ++;
          else if([log.status >= 400]) cantidadServidor1_error ++;
        }
      });

      console.log("Logs Servidor 1 (info):", cantidadServidor1_info);
      console.log("Logs Servidor 1 (warn):", cantidadServidor1_warn);
      console.log("Logs Servidor 1 (error):", cantidadServidor1_error);
      console.log("Logs Servidor 2 (info):", cantidadServidor2_info);
      console.log("Logs Servidor 2 (warn):", cantidadServidor2_warn);
      console.log("Logs Servidor 2 (error):", cantidadServidor2_error);

        const labels = ["Servidor 1", "Servidor 2"];
        const valoresInfo = [cantidadServidor1_info, cantidadServidor2_info];
        const valoresWarn = [cantidadServidor1_warn, cantidadServidor2_warn];
        const valoresError = [cantidadServidor1_error, cantidadServidor2_error];

        setChartData({
            labels,
            datasets: [
                {
                    label: "Logs con nivel info",
                    data: valoresInfo,
                    backgroundColor: "rgba(54, 162, 235, 0.5)",
                    borderColor: "rgba(54, 162, 235, 1)",
                    borderWidth: 1,
                },
                {
                  label: "Logs con nivel warn",
                  data: valoresWarn,
                  backgroundColor: "rgba(255, 159, 64, 0.5)",
                  borderColor: "rgba(255, 159, 64, 1)",
                  borderWidth: 1,
              },
              {
                label: "Logs con nivel error",
                data: valoresError,
                backgroundColor: "rgba(255, 99, 132, 0.5)",
                borderColor: "rgba(255, 99, 132, 1)",
                borderWidth: 1,
            }
            ]
        });
    };

    return (
        <div className="container mt-4">
            <h3 className="text-center">Logs</h3> 
            <div className="card p-3">
                {chartData ? (
                    <Bar 
                        data={chartData} 
                        options={{
                            responsive: true, 
                            plugins: { 
                                legend: { position: 'top' } 
                            }
                        }} 
                    />
                ) : (
                    <p>Cargando datos...</p>
                )}
            </div>

            <button 
                className="btn btn-warning mt-3"  
                type="button" 
                onClick={() => navigate('/home')}
            >
                Home
            </button>
        </div>
    );
};

export default Logs;
